          </Modal.Header>
          <Modal.Body>
            {AddDevice}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={()=>{addNew();}}>
              Save Changes
            </Button>
            <Button variant="secondary" onClick={updateClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
        {status_dis}
        <Modal show={upsta} onHide={upstaClose}>
          <Modal.Header closeButton>
            <Modal.Title>Update Status</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {UpdateStatus}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={()=>{addStatus();}}>
              Save Changes
            </Button>
            <Button variant="secondary" onClick={upstaClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
        {note_dis}
        <Modal show={upnot} onHide={upnotClose}>
          <Modal.Header closeButton>
            <Modal.Title>Update Note</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {UpdateNote}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={()=>{addNote();}}>
              Save Changes
            </Button>
            <Button variant="secondary" onClick={upnotClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
        <table className="table table-light table-striped table-hover">
            <thead>
                <tr>
                  <th>#</th>
                  {
                  // create td elements from fieldsKeys
                  keys.map((k, idx2) => <th key={idx2}>{k}</th>)
                  }
                </tr>
            </thead>
            <tbody>              
                {DisplayData}
                <Modal show={ButtonPop} onHide={handleClose}  className="fade modal-lg">
                  <Modal.Header closeButton>
                    <Modal.Title>{uid}</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <div className="inner-table">
                      <table className="table table-light table-striped table-hover">
                        <thead>
                            <tr>
                              <th>#</th>
                              <th>UID</th>
                              <th>Status</th>
                              <th>Borrower</th>
                              <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>              
                            {inda.map(
                              (info, i)=>{
                                  return( 
                                      <tr key={i} >
                                          <td>{i}</td>
                                          <td>{info.UID}</td>
                                          <td>{info.Status}</td>
                                          <td>{info.Borrower}</td>
                                          <td>{info.Date}</td>
                                      </tr>
                                  )
                              })
                            }
                        </tbody>
                      </table>
                      <p>Note: {inda[0].Note}</p>
                    </div>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}> Modify Device Info </Button>
                    <Button variant="secondary" onClick={handleClose}> Close </Button>
                  </Modal.Footer>
                </Modal>
            </tbody>
        </table>
      </div>
    </div>
  );

}
