import './Tutorial.css';
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';

export default function Tutorial(props) {
  let p1 = require("./Picture1.png");
  let p2 = require("./Picture2.png");
  let p3 = require("./Picture3.png");
  let p4 = require("./Picture4.png");
  let p5 = require("./Picture5.png");

  let rule_text = <></>;
  if(props.cate === "Borrow") {
    rule_text = <>
                  <h3>Borrow Cases</h3>
                  <h6>Case 1 - NICs/Modules/Cables(Return on the same day)</h6>
                  <h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{">>"} No need to update in AIMT</h6>
                  <h6>Case 2 - NICs/Modules/Cables</h6>
                  <h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{">>"} Update in AIMT</h6>
                  <h6>Case3 - Tools</h6>
                  <h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{">>"} Update in AIMT</h6>
                  <h6>Case4 - AIMT owner is not in seat</h6>
                  <h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{">>"}  IM AIMT owner first, or inform AIMT owner by mail</h6>
                </>
  } else {
    rule_text = <>
                  <h3>Return Rule</h3>
                  <h6>AIMT owner check with borrower if NIC is functional or not</h6>
                  <h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{">>"} If broken, please update in AIMT</h6>
                </>
  }
  let navigate = useNavigate(); 
  return (
    <div className="App">
      <header className="tu-title1"> ANIL Inventory Management Tool </header>
      <header className="tu-title2"> How to update information/notes </header>
      <div className="outer" style={{width: '50%', float:'left'}}>
        <div className="in-text"><h4>·&nbsp;&nbsp;&nbsp;&nbsp;Update notes</h4></div>
        <div className="in-text"><h6>Step1 – Find out the device and click it</h6></div>
        <img src={p1} className="tu_image" alt="n1" />
        <img src={p2} className="tu_image" alt="n2" />
        <img src={p3} className="tu_image" alt="n3" />
        <img src={p4} className="tu_image" alt="n4" />
        <img src={p4} className="tu_image" alt="n5" />
      </div>
      <div className="outer" style={{width: '50%', float:'right'}}>
        <img src={p1} className="tu_image" alt="n1" />
        <img src={p2} className="tu_image" alt="n2" />
        <img src={p3} className="tu_image" alt="n3" />
        <img src={p4} className="tu_image" alt="n4" />
        <img src={p4} className="tu_image" alt="n5" />

      </div>
      <Button onClick={()=>{navigate("/");}} variant="primary rule-btn">Back</Button>
    </div>
  );

}
