import './Show.css';
import React from 'react';
// import Popup from '../popup/Popup';
import { Button, Modal } from 'react-bootstrap';
import { useState, useEffect, useRef } from 'react';

export default function Show(props) {
  const renderAfterCalled = useRef(false);
  const [uid, setuid] = useState();
  const [da, setDa] = useState();
  const [inda, setInda] = useState();
  const [update,setUpdate] = useState(false);
  const updateClose = () => setUpdate(false);
  const updateShow = () => setUpdate(true);
  const [ButtonPop,setButtonPop] = useState(false);
  const handleClose = () => setButtonPop(false);
  const handleShow = (e) => {setButtonPop(true); setuid(e.target.parentElement.innerText.split('\t')[1]);};

  const fetchUID = async () => {
    return fetch('/api/uid', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ask: props.value, curuid: uid})
    })
      .then(response => response.json())
      .then(data => setInda(data))
   }   

  useEffect(
  () => {
    if (!renderAfterCalled.current) {
      fetch('/api/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ask: props.value})
      })
      .then(response => response.json())
      .then(data => setDa(data))

    }

    renderAfterCalled.current = true;
  }, [props]) 

  let showtitle = "";
  let DisplayData, keys;

  if(props.value.substring(0, 3) === "ada"){
    showtitle = "Series "+props.value.substring(3);
  } else {
    showtitle = props.value.charAt(0).toUpperCase() + props.value.slice(1);;
  }

  if (da === undefined || da.length === 0) {
    return (
      <div className="App">
        <header className="App-title">{showtitle}</header>
        <div className="App-header">
          <h1>There is no data.</h1>
        </div>
      </div>
    );
  } else {
    keys = Object.keys(da[0])
    DisplayData=da.map(
      (info, i)=>{
          return( 
              <tr key={i} onClick={(e)=>{handleShow(e); fetchUID();}}>
                  <td>{i}</td>
                  {
                  keys.map((k, idx2) => <td key={idx2} >{info[k]}</td>)
                  }
              </tr>
          )
      }
    )
  }



  if (inda === undefined || inda === null) {
    return (
        <div className="App">
          <header className="App-title">{showtitle}</header>
          <div className="App-header">
            <Button onClick={updateShow}>Update Status</Button>
            <Modal show={update} onHide={updateClose}>
              <Modal.Header closeButton>
                <Modal.Title>Update Status</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                hi
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={updateClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={updateClose}>
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
            <table className="table table-light table-striped table-hover">
                <thead>
                    <tr>
                      <th>#</th>
                      {
                      // create td elements from fieldsKeys
                      keys.map((k, idx2) => <th key={idx2}>{k}</th>)
                      }
                    </tr>
                </thead>
                <tbody>              
                    {DisplayData}
                    <Modal show={ButtonPop} onHide={handleClose}>
                      <Modal.Header closeButton>
                        <Modal.Title>Detail</Modal.Title>
                      </Modal.Header>
                      <Modal.Body>
                        hi
                      </Modal.Body>
                      <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                          Close
                        </Button>
                      </Modal.Footer>
                    </Modal>
                </tbody>
            </table>
          </div>
        </div>
      );
  } 

  return (
    <div className="App">
      <header className="App-title">{showtitle}</header>
      <div className="App-header">
        <Button onClick={updateShow}>Update Status</Button>
        <Modal show={update} onHide={updateClose}>
          <Modal.Header closeButton>
            <Modal.Title>Update Status</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            hi
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={updateClose}>
              Close
            </Button>
            <Button variant="primary" onClick={updateClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
        <table className="table table-light table-striped table-hover">
            <thead>
                <tr>
                  <th>#</th>
                  {
                  // create td elements from fieldsKeys
                  keys.map((k, idx2) => <th key={idx2}>{k}</th>)
                  }
                </tr>
            </thead>
            <tbody>              
                {DisplayData}
                <Modal show={ButtonPop} onHide={handleClose}  className="fade modal-lg">
                  <Modal.Header closeButton>
                    <Modal.Title>{uid}</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <div className="inner-table">
                      <table className="table table-light table-striped table-hover">
                        <thead>
                            <tr>
                              <th>#</th>
                              <th>UID</th>
                              <th>Status</th>
                              <th>Borrower</th>
                              <th>Date</th>
                              <th>Note</th>
                            </tr>
                        </thead>
                        <tbody>              
                            {inda.map(
                              (info, i)=>{
                                  return( 
                                      <tr key={i} >
                                          <td>{i}</td>
                                          <td>{info.UID}</td>
                                          <td>{info.Status}</td>
                                          <td>{info.Borrower}</td>
                                          <td>{info.Date}</td>
                                          <td>{info.Note}</td>
                                      </tr>
                                  )
                              })
                            }
                        </tbody>
                      </table>
                    </div>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                      Close
                    </Button>
                  </Modal.Footer>
                </Modal>
            </tbody>
        </table>
      </div>
    </div>
  );

}
