                        </label>
                        <label>
                          <p className="text" htmlFor="Interface">New Interface</p>
                          <input type="text" onChange={e => setInterface(e.target.value)} id="interface"/>
                        </label>
                      </form>
                    </div>
                  </>
  } else if(props.value === "tools"){
