import './Show.css';
import React from 'react';

export default function Show(props) {
  const renderAfterCalled = React.useRef(false);
  const [da, setDa] = React.useState();

  React.useEffect(
  () => {
    if (!renderAfterCalled.current) {
      fetch('http://localhost:5000/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ask: props.value})
      })
      .then(response => response.json())
      .then(data => setDa(data))

    }

    renderAfterCalled.current = true;
  }, [props]) 

  let showtitle = "";
  let DisplayData, keys;
  if(props.value.substring(0, 3) === "ada"){
    showtitle = "Series "+props.value.substring(3)
    if (da === undefined) {
      return <>Still loading...</>;
    } else {
      keys = Object.keys(da[0])
      DisplayData=da.map(
        (info, i)=>{
            return( 
                <tr key={i}>
                    <td>{i}</td>
                    {
                    keys.map((k, idx2) => <td key={idx2}>{info[k]}</td>)
                    }
                </tr>
            )
        }
      )
    }
  }

  if (da === undefined) {
    return <>Still loading...</>;
  }   

  return (
    <div className="App">
      <header className="App-title">{showtitle}</header>
      <div className="App-header">
        <table className="table table-light table-striped">
            <thead>
                <tr>
                  <td>idx</td>
                  {
                  // create td elements from fieldsKeys
                  keys.map((k, idx2) => <td key={idx2}>{k}</td>)
                  }
                </tr>
            </thead>
            <tbody>              
                {DisplayData}
            </tbody>
        </table>
      </div>
    </div>
  );

}
