import './Show.css';
import React from 'react';

export default function Show(props) {
  const renderAfterCalled = React.useRef(false);
  const [da, setDa] = React.useState();

  React.useEffect(
  () => {
    if (!renderAfterCalled.current) {
      fetch('http://localhost:5000/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ask: props.value})
      })
      .then(response => response.json())
      .then(data => setDa(data))

    }

    renderAfterCalled.current = true;
  }, [props]) 

  let showtitle = "";
  let DisplayData;
  if(props.value.substring(0, 3) === "ada"){
    showtitle = "Series "+props.value.substring(3)
    if (da === undefined) {
      return <>Still loading...</>;
    } else {
      DisplayData=da.map(
        (info)=>{
            return(
                <tr>
                    <td>{info.UID}</td>
                    <td>{info.Family}</td>
                </tr>
            )
        }
      )
    }
  }



  return (
    <div className="App">
      <header className="App-title">{showtitle}</header>
      <div className="App-header">
        <table className="table table-striped">
            <thead>
                <tr>
                <th>UID</th>
                <th>Family</th>
                </tr>
            </thead>
            <tbody>              
                {DisplayData}
            </tbody>
        </table>
      </div>
    </div>
  );

}
