import './Show.css';
import React from 'react';
// import Popup from '../popup/Popup';
import { Button, Modal } from 'react-bootstrap';
import { useState, useEffect, useRef } from 'react';

export default function Show(props) {
  const renderAfterCalled = useRef(false);
  const [uid, setuid] = useState();
  const [da, setDa] = useState();
  const [inda, setInda] = useState();
  const [ButtonPop,setButtonPop] = useState(false);
  const handleClose = () => setButtonPop(false);
  const handleShow = (e) => {setButtonPop(true); setuid(e.target.parentElement.innerText.split('\t')[1]);};

  const fetchUID = async () => {
    return fetch('http://localhost:5000/uid', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ask: props.value, curuid: uid})
    })
      .then(response => response.json())
      .then(data => setInda(data))
   }   

  useEffect(
  () => {
    if (!renderAfterCalled.current) {
      fetch('http://localhost:5000/show', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ask: props.value})
      })
      .then(response => response.json())
      .then(data => setDa(data))

    }

    renderAfterCalled.current = true;
  }, [props]) 

  let showtitle = "";
  let DisplayData, keys;
  if(props.value.substring(0, 3) === "ada"){
    showtitle = "Series "+props.value.substring(3)
    if (da === undefined) {
      return <>Still loading...</>;
    } else {
      keys = Object.keys(da[0])
      DisplayData=da.map(
        (info, i)=>{
            return( 
                <tr key={i} onClick={(e)=>{handleShow(e); fetchUID();}}>
                    <td>{i}</td>
                    {
                    keys.map((k, idx2) => <td key={idx2} >{info[k]}</td>)
                    }
                </tr>
            )
        }
      )
    }
  }

  if (da === undefined || da === null) {
    return <>Still loading...</>;
  }   

  if (inda === undefined || inda === null) {
    return (
        <div className="App">
          <header className="App-title">{showtitle}</header>
          <div className="App-header">
            <table className="table table-light table-striped table-hover">
                <thead>
                    <tr>
                      <th>#</th>
                      {
                      // create td elements from fieldsKeys
                      keys.map((k, idx2) => <th key={idx2}>{k}</th>)
                      }
                    </tr>
                </thead>
                <tbody>              
                    {DisplayData}
                    <Modal show={ButtonPop} onHide={handleClose}>
                      <Modal.Header closeButton>
                        <Modal.Title>Detail</Modal.Title>
                      </Modal.Header>
                      <Modal.Body>
                        hi
                      </Modal.Body>
                      <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                          Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                          Save Changes
                        </Button>
                      </Modal.Footer>
                    </Modal>
                </tbody>
            </table>
          </div>
        </div>
      );
  } 

  return (
    <div className="App">
      <header className="App-title">{showtitle}</header>
      <div className="App-header">
        <table className="table table-light table-striped table-hover">
            <thead>
                <tr>
                  <th>#</th>
                  {
                  // create td elements from fieldsKeys
                  keys.map((k, idx2) => <th key={idx2}>{k}</th>)
                  }
                </tr>
            </thead>
            <tbody>              
                {DisplayData}
                <Modal show={ButtonPop} onHide={handleClose}>
                  <Modal.Header closeButton>
                    <Modal.Title>Detail</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <table className="table table-light table-striped table-hover">
                      <thead>
                          <tr>
                            <th>#</th>
                            <th>UID</th>
                            <th>Status</th>
                            <th>Borrower</th>
                            <th>Date</th>
                            <th>Note</th>
                          </tr>
                      </thead>
                      <tbody>              
                          {inda.map(
                            (info, i)=>{
                                return( 
                                    <tr key={i} >
                                        <td>{i}</td>
                                        <td>{info.UID}</td>
                                        <td>{info.Status}</td>
                                        <td>{info.Borrower}</td>
                                        <td>{info.Date}</td>
                                        <td>{info.Note}</td>
                                    </tr>
                                )
                            })
                          }
                      </tbody>
                  </table>
                </Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                      Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                      Save Changes
                    </Button>
                  </Modal.Footer>
                </Modal>
            </tbody>
        </table>
      </div>
    </div>
  );

}
